
const axios = require('axios')
const baseUrl = 'http://localhost:3000/v1/'

export const fetchLists = async () => {
    const response = await axios.get(baseUrl + 'lists')
    return response.data
}

export const fetchListTasks = async (listId) => {
    const response = await axios.get(baseUrl + 'list_tasks/' + listId)
    return response.data
}

export const createTask = async (content, listId) => {
    const response = await axios.post(baseUrl + 'tasks/', {task:{content: content, list_id: listId}})
    return response.data
}