
const axios = require('axios')
const baseUrl = 'http://localhost:3000/v1/'

export const fetchLists = async () => {
    const response = await axios.get(baseUrl + 'lists')
    return response.data
}

export const fetchListTasks = async (listId) => {
    const response = await axios.get(baseUrl + 'list_tasks/' + listId)
    return response.data
}

export const createTask = async (content, listId) => {
    const response = await axios.post(baseUrl + 'tasks/', {task:{content: content, list_id: listId}})
    return response.data
}

export const deleteTask = async (taskId) => {
    const response = await axios.delete(baseUrl + 'tasks/' + taskId)
    return response.data
}

export const toggleDoneStatus = async (taskId) => {
    const response = await axios.patch(baseUrl + 'tasks/' + taskId + '/check')
    return response.data
}

export const taskPositionUp = async (taskId) => {
    const response = await axios.patch(baseUrl + 'tasks/' + taskId + '/up')
    return response.data
}

export const taskPositionDown = async (taskId) => {
    const response = await axios.patch(baseUrl + 'tasks/' + taskId + '/down')
    return response.data
}
