import React, {Component} from 'react'
import {connect} from "react-redux";

import {
    createTask,
    fetchListTasks
} from 'actions'
import {getTasksOfList} from "selectors";

class NewTaskSection extends Component{
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(event) {
        this.setState({value: event.target.value})
    }

    handleSubmit(event){
        event.preventDefault();
        this.props.createTask(this.state.value, this.props.listId)

    }

    render(){
        return(
            <div className="w3-row w3-flat-clouds add-task-section">
                <div className="w3-col w3-xxlarge lst-icon-main">
                    <i className="fa fa-plus"></i>
                </div>

                <form onSubmit={this.handleSubmit}>
                    <div className="w3-col w3-right btn-wrapper">
                        <button className="w3-btn w3-ripple w3-flat-nephritis"

                        >Add Task</button>
                    </div>
                    <div className="w3-rest">
                        <input className="w3-input"
                               type="text"
                               placeholder="Start typing here to create a task..."
                               onChange={this.handleChange}

                        />
                    </div>
                </form>
            </div>
        )
    }
}
// const mapStateToProps = (state, ownProps) => {
//     return {
//         tasks: getTasksOfList(state, ownProps.listId)
//     }
// }

const mapDispatchToProps = {
    createTask,
    fetchListTasks
}

export default connect(null, mapDispatchToProps)(NewTaskSection)