import React, {Component} from 'react'
import {connect} from 'react-redux'
import {
    map
} from 'ramda'

import TasksOfList from 'components/tasksOfList'
import {
    fetchLists,
} from 'actions'

import {
    getLists
} from 'selectors'

class Tasklists extends Component {
    componentDidMount () {
        this.props.fetchLists()
    }

    renderList(list){
        return (
            <div className='panel panel-primary task-list' key={list.id}>
                <div className='panel-heading task-list-header'>
                    <span className="glyphicon glyphicon-list-alt"></span>{list.label}

                    <span className="glyphicon glyphicon-pencil pull-right"></span>
                    <span className="glyphicon glyphicon-trash pull-right"></span>
                </div>

                <div className="panel-body">
                    <span className="glyphicon glyphicon-plus"></span>

                    <form class="form-horizontal" action="">
                        <input type="text" class="form-control input-sm" id="newTask" placeholder="Start typing here to create a task" name="task-input"/>
                        <button type="submit" class="btn btn-success ">Add Task</button>
                    </form>
                </div>

                <div className='tasks-wraper'>
                    <TasksOfList listId = {list.id} />
                </div>
            </div>
        )
    }

    render() {
        const {lists} = this.props
        return (
            <main>
                <div className="container">
                    {lists.map((list) => this.renderList(list))}
                </div>
            </main>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        lists: getLists(state)
    }
}

const mapDispatchToProps = {
    fetchLists
}

export default connect(mapStateToProps, mapDispatchToProps)(Tasklists)