import React, {Component} from 'react'
import {connect} from 'react-redux'
import {
    map
} from 'ramda'

import TasksOfList from 'components/tasksOfList'
import {
    fetchLists,
} from 'actions'

import {
    getLists
} from 'selectors'

class Tasklists extends Component {
    componentDidMount () {
        this.props.fetchLists()
    }

    renderList(list){
        return (
            <div className='container task-list' key={list.id}>
                <div className='task-list-header'>
                    <span className="glyphicon glyphicon-list">{list.label}</span>
                    {/*<h4>{list.label}</h4>*/}
                </div>
                <div className='tasks-wraper'>
                    <TasksOfList listId = {list.id} />
                </div>
            </div>
        )
    }

    render() {
        const {lists} = this.props
        return (
            <main>
                {lists.map((list) => this.renderList(list))}
                {/*{Object.assign(...Object.keys(lists).map(k => (this.renderList(lists[k]))))}*/}
            </main>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        lists: getLists(state)
    }
}

const mapDispatchToProps = {
    fetchLists
}

export default connect(mapStateToProps, mapDispatchToProps)(Tasklists)