import React, {Component} from 'react'
import {connect} from 'react-redux'

import TasksOfList from 'components/tasksOfList'
import NewTaskSection from 'components/newTaskSection'
import {
    fetchLists,
    createList,
    deleteList,
} from 'actions'

import {
    getLists
} from 'selectors'

class Tasklists extends Component {
    componentDidMount () {
        this.props.fetchLists()
    }

    createList = (name) => this.props.createList(name)
    deleteList = (id) => this.props.deleteList(id)


    renderList(list){
        return (
            <div className="w3-container w3-section w3-card-4 lst-container"  key={list.id}>
                <div className='w3-bar w3-flat-belize-hole lst-header'>
                    <div className='w3-bar w3-flat-belize-hole lst-header'>
                        <div className="w3-bar-item w3-xlarge lst-icon-main">
                            <i className="fa fa-calendar"></i>
                        </div>
                        <div className="w3-bar-item w3-padding-small">
                            <h4>{list.label}</h4>
                        </div>
                        <div className="w3-bar-item w3-right">
                            <div className="w3-row w3-xlarge">
                                <div className="w3-half w3-border-right w3-border-grey w3-hover-text-dark-grey lst-icon-edit">
                                    <i className="fa fa-pencil"></i>
                                </div>
                                <div className="w3-half lst-icon-edit w3-hover-text-deep-orange">
                                    <i className="fa fa-trash-o"
                                       onClick={this.deleteList.bind(this, list.id)}
                                    ></i>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <NewTaskSection listId = {list.id} />

                <TasksOfList listId = {list.id} />
            </div>
        )
    }

    render() {
        const {lists} = this.props
        return (
            <div>
                <div className="w3-container w3-center">
                    <h1>ToDo List</h1>
                </div>

                {lists.map((list) => this.renderList(list))}

                <div className="w3-center">
                    <button
                        className="w3-btn w3-round w3-ripple w3-flat-nephritis"
                        onClick={this.createList.bind(this, 'New Tasklist')}
                    >
                        Add List
                    </button>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        lists: getLists(state)
    }
}

const mapDispatchToProps = {
    fetchLists,
    createList,
    deleteList,
}

export default connect(mapStateToProps, mapDispatchToProps)(Tasklists)