import React, {Component} from 'react'
import {connect} from 'react-redux'

import TasksOfList from 'components/tasksOfList'
import NewTaskSection from 'components/newTaskSection'
import {
    fetchLists,
} from 'actions'

import {
    getLists
} from 'selectors'

class Tasklists extends Component {
    componentDidMount () {
        this.props.fetchLists()
    }

    renderList(list){
        return (
            <div className="w3-container w3-section w3-card-4 lst-container"  key={list.id}>
                <div className='w3-bar w3-flat-belize-hole lst-header'>
                    <div className='w3-bar w3-flat-belize-hole lst-header'>
                        <div className="w3-bar-item w3-xlarge lst-icon-main">
                            <i className="fa fa-calendar"></i>
                        </div>
                        <div className="w3-bar-item w3-padding-small">
                            <h4>{list.label}</h4>
                        </div>
                        <div className="w3-bar-item w3-xlarge w3-right lst-icon-edit">
                            <i className="fa fa-pencil"> |</i>
                            <i className="fa fa-trash-o"></i>
                        </div>
                    </div>
                </div>

                <NewTaskSection listId = {list.id} />

                <TasksOfList listId = {list.id} />
            </div>
        )
    }

    render() {
        const {lists} = this.props
        return (
            <div>
                <div className="w3-container w3-center">
                    <h1>ToDo List</h1>
                </div>
                {lists.map((list) => this.renderList(list))}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        lists: getLists(state)
    }
}

const mapDispatchToProps = {
    fetchLists
}

export default connect(mapStateToProps, mapDispatchToProps)(Tasklists)