import {
    TASK_EDIT_START,
    TASK_EDIT_FINISH,
} from 'actionTypes'


const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case TASK_EDIT_START:
            return payload
        case TASK_EDIT_FINISH:
            const {id, position, is_done, list_id, created_at, updated_at } = state
            return {id: id, content: payload, position: position, is_done: is_done, list_id: list_id, created_at: created_at, updated_at: updated_at}
        default:
            return state
    }
}