import {
    indexBy,
    merge,
    prop
} from 'ramda'

import {
    FETCH_LISTS_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_LISTS_SUCCESS:
            const lists = indexBy(prop('id'), payload)
            return merge(state, lists)
        default:
            return state
    }
}