import {
    indexBy,
    prop
} from 'ramda'

import {
    FETCH_LISTS_SUCCESS
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_LISTS_SUCCESS:
            // const lists = Object.values(indexBy(prop('id'), payload))
            const  lists = payload
            return lists
        default:
            return state
    }
}