import {
    indexBy,
    merge,
    prop,
    append
} from 'ramda'

import {
    FETCH_LISTS_SUCCESS,
    FETCH_LIST_TASKS_SUCCESS
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_LISTS_SUCCESS:
            const lists = indexBy(prop('id'), payload)
            return (lists)
        case FETCH_LIST_TASKS_SUCCESS:
            const stateList = state
            const listId = payload[0].list_id


            return
        default:
            return state
    }
}