import {
    append, indexBy, prop
} from 'ramda'

import {
    FETCH_LIST_TASKS_SUCCESS,
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_LIST_TASKS_SUCCESS:
            const tasks = state.concat(payload)
            return tasks
        default:
            return state
    }
}