import {
    FETCH_LIST_TASKS_SUCCESS,
    TASK_CREATE_SUCCESS
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_LIST_TASKS_SUCCESS:
            return state.concat(payload)
        case TASK_CREATE_SUCCESS:
            return [...state, payload]
        default:
            return state
    }
}